import React, { useState } from "react";
import axios from "axios";

export default function Summary() {
  const [summary, setSummary] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSummarizeFeedback = async () => {
    setIsLoading(true);
    setError(null);

    try {
      // No body needed because backend reads CSV directly
      const response = await axios.post("http://127.0.0.1:8000/generate-summary");
      setSummary(response.data.summary);
    } catch (err) {
      setError("Failed to generate summary. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="p-4 bg-white shadow rounded">
      <h2 className="text-xl font-semibold mb-4">üìù Feedback Summary</h2>

      <button
        onClick={handleSummarizeFeedback}
        className="bg-blue-600 text-white p-2 rounded mb-4 hover:bg-blue-700"
      >
        Summarize Feedback
      </button>

      {isLoading && <p>Generating summary...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {summary && (
        <div className="mt-4 p-4 bg-gray-100 rounded">
          <h3 className="text-lg font-semibold">Summary</h3>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}
